mapper.select:
select l.id, l.customerId,c.openid,c.nickName,l.status,l.no,l.createTime
from t_lottery l
left join t_customer c on c.id = l.customerId


		and l.customerId = #{customerId}


		and c.openid = #{openid}


		and c.nickName like concat('%',#{nickName},'%')


		and l.status = #{status}


		 and l.createTime >= #{createTimeStart} 


		 and l.createTime <= #{createTimeEnd}
